mapper.select:
	select * from T_MAM_MEASOBJ_REPORT


			and id = #{id}


			and instr(name,#{name})>0


			and object_id = #{objectId}


			and tunnel_id = #{tunnelId}


			and object_type = #{objectType}


        and object_type in 

            #{item}



			and time_type = #{timeType}


			and s_time > = #{startTime}


			and e_time < = #{endTime}


	order by s_time desc
