mapper.select:
SELECT * FROM T_MAM_MEASVALUE_DI


		and OBJECT_ID = #{objectId}


		and time >=#{startTime}


		and time <=#{endTime}

mapper.insert:
insert into T_MAM_MEASVALUE_DI (time, Object_ID, cv,status,statusc) select a.*,0,0 from (
select #{item.time}, #{item.objectId}, #{item.CV} from dual
)a
